// ProductDetail.js
import React, { useState } from 'react';
import axios from 'axios';

function ProductDetail({ productSlug }) {
    const [message, setMessage] = useState('');
    const [cartItemsHtml, setCartItemsHtml] = useState('');

    const handleAddToCart = (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('product_slug', productSlug);
        formData.append('quantity', 1);

        axios.post(`/cart_add/${productSlug}/`, formData)
            .then(response => {
                setMessage(response.data.message);
                setCartItemsHtml(response.data.cart_items_html);
            })
            .catch(error => {
                console.error('Ошибка при добавлении товара в корзину:', error);
            });
    };

    return (
        <div>
            <h1>Product Detail</h1>
            <form onSubmit={handleAddToCart}>
                <button type="submit" className="btn btn-dark add-to-cart">Добавить в корзину</button>
            </form>
            {message && <p>{message}</p>}
            <div dangerouslySetInnerHTML={{ __html: cartItemsHtml }} />
        </div>
    );
}

export default ProductDetail;
